import React, { Component } from 'react';
import css from './ImageGallery.module.css';

export class ImageGallery extends Component {
  state = {
    imagesSearch: null,
    page: 1,
    loding: false,
    error: null,
    status: {
      idle: 1,
    },
  };

  componentDidUpdate(prevProps, prevState) {
    const prevName = prevProps.searchName;
    const newName = this.props.searchName;

    const KEY = '37071230-d6b04d3068f1a0950a5b376a5';
    const page = this.state.page;
    const name = newName.searchName;

    if (prevName !== newName) {
      this.setState({ status: pending, imagesSearch: null });

      setTimeout(() => {
        fetch(
          `https://pixabay.com/api/?q=${name}&page=${page}&key=${KEY}&image_type=photo&orientation=horizontal&per_page=12`
        )
          .then(res => {
            if (res.ok) {
              return res.json();
            }
            return Promise.reject(new Error('Пусто 2'));
          })
          .then(data => this.setState({ imagesSearch: data.hits }))
          .catch(error => this.setState({ error }))
          .finally(() => this.setState({ loding: false }));
      }, 1000);
    }
  }

  render() {
    const { imagesSearch, status } = this.state;
    console.log('imagesSearch: ', imagesSearch);

    if (status === 'pending') {
      return <div>Загрузка</div>;
    }

    if (status === 'resovled') {
      return (
        <ul className={css.ImageGallery}>
          {imagesSearch.map(({ id, largeImageURL, tags }) => (
              <li key={id}>
                <img src={largeImageURL} alt={tags} />
              </li>
            ))}
        </ul>
      );
    }
  }
}

