// ImageGallery.js
import React, { Component } from 'react';
import css from './ImageGallery.module.css';
import Button from '../Button/';

export class ImageGallery extends Component {
  state = {
    imagesSearch: null,
    page: 1,
    loading: false,
    error: null,
    status: 'idle',
  };

  componentDidUpdate(prevProps, prevState) {
    const prevName = prevProps.searchName;
    const newName = this.props.searchName;

    const KEY = '37071230-d6b04d3068f1a0950a5b376a5';
    const page = this.state.page;
    const name = newName;

    if (prevName !== newName) {
      this.setState({ status: 'pending', imagesSearch: null });

      setTimeout(() => {
        fetch(
          `https://pixabay.com/api/?q=${name}&page=${page}&key=${KEY}&image_type=photo&orientation=horizontal&per_page=12`
        )
          .then(res => {
            if (res.ok) {
              return res.json();
            }
            return Promise.reject(new Error('Пусто 2'));
          })
          .then(data =>
            this.setState(prevState => ({
              imagesSearch: prevState.imagesSearch
                ? [...prevState.imagesSearch, ...data.hits]
                : data.hits,
              status: 'resolved',
              page: prevState.page + 1,
            }))
          )
          .catch(error => this.setState({ error }))
          .finally(() => this.setState({ loading: false }));
      }, 1000);
    }
  }

  handleLoadMore = () => {
    this.setState({ loading: true });
  };

  render() {
    const { imagesSearch, status, loading } = this.state;
    console.log('imagesSearch: ', imagesSearch);

    if (status === 'pending') {
      return <div>Загрузка</div>;
    }

    if (status === 'rejected') {
      return <div>Все плохо</div>;
    }

    if (status === 'resolved') {
      return (
        <div>
          <ul className={css.ImageGallery}>
            {imagesSearch.map(({ id, largeImageURL, tags }) => (
              <li key={id}>
                <img src={largeImageURL} alt={tags} />
              </li>
            ))}
          </ul>
          {imagesSearch.length > 0 && !loading && (
            <Button onClick={this.handleLoadMore} />
          )}
          {loading && <div>Загрузка...</div>}
        </div>
      );
    }
  }
}
