import React, { Component } from 'react';
import { Searchbar } from './Searchbar/Searchbar';

export class App extends Component {
  state = {
    searchName: '',
    images: null,
    page: 1,
    loding: false,

  }

  userRequest = dataUser => { 
    console.log('dataUser: ', dataUser);
    console.log(this.state.searchName);
  }

  API = ()=> {
    const KEY = '37071230-d6b04d3068f1a0950a5b376a5';
    const page = this.state.page;
    this.setState({loding: true});
    fetch(
      `https://pixabay.com/api/?q=cat&page=${page}&key=${KEY}&image_type=photo&orientation=horizontal&per_page=12`
    )
      .then(res => res.json())
      .then(data => console.log('data: ', data))
      .finally(() => this.setState({ loding: false }));
  }

  componentDidMount() {
    this.API;
  }
  render() {
    
    return (
      <div>
        <Searchbar userRequest={this.userRequest} />
        <li >
          <img src="" alt="" />
        </li>
      </div>
    );

  }
}
