import React, { Component } from 'react';
import { Searchbar } from './Searchbar/Searchbar';

export class App extends Component {
  state = {
    searchName: '',
    images: null,
    page: 1,
    loding: false,

  }

  userRequest = dataUser => { 
    const normalizedSearchName = dataUser.searchName.toLowerCase();
    this.setState({ searchName: normalizedSearchName });
    

  }

  API = ()=> {
    const KEY = '37071230-d6b04d3068f1a0950a5b376a5';
    const page = this.state.page;
    const name = this.state.searchName;
    this.setState({loding: true});
    fetch(
      `https://pixabay.com/api/?q=${name}&page=${page}&key=${KEY}&image_type=photo&orientation=horizontal&per_page=12`
    )
      .then(res => res.json())
      .then(data => this.setState({ images: data.hits }))
      .finally(() => this.setState({ loding: false }));
  }

  // componentDidMount() {
  //   this.API()
  // }
  render() {
    
    const imagesAnswer = this.state.images;
    console.log('imagesAnswer: ', imagesAnswer);
    return (
      <div>
        <Searchbar userRequest={this.userRequest} />
        <div>
          {imagesAnswer &&
            imagesAnswer.map(
              ({ id, largeImageURL, previewHeight, previewWidth, tags }) => (
                <li className="gallery-item" key={id}>
                  <img
                    width={previewWidth}
                    height={previewHeight}
                    src={largeImageURL}
                    alt={tags}
                  />
                </li>
              )
            )}
        </div>
      </div>
    );

  }
}
