import React, { Component } from 'react';
import css from './ImageGallery.module.css';
import { Button } from 'components/Button/Button';
import { ImageGalleryItem } from 'components/ImageGalleryItem/ImageGalleryItem';

export class ImageGallery extends Component {
  state = {
    searchName: null,
    imagesSearch: null,
    page: 1,
    loading: false,
    error: null,
    status: 'idle',
  };

  componentDidUpdate(prevProps, prevState) {
    const prevName = prevProps.searchName;
    const newName = this.props.searchName;

    const KEY = '37071230-d6b04d3068f1a0950a5b376a5';
    const page = this.state.page;
    console.log('page: ', page);
    const name = newName.searchName;

    if (prevName !== newName) {
      this.setState({ searchName: null });
      console.log('пид');
      this.fetchImages(name, page, KEY);
    }
  }

  fetchImages = (name, page, key) => {
    console.log('page: ', page);
    this.setState({ loading: true });
    console.log([...prevState.imagesSearch, ...data.hits]);
    fetch(
      `https://pixabay.com/api/?q=${name}&page=${page}&key=${key}&image_type=photo&orientation=horizontal&per_page=12`
    )
      .then(res => {
        if (res.ok) {
          return res.json();
        }
        return Promise.reject(new Error('Пусто 2'));
      })
      .then(data => {
        this.setState(prevState => ({
          imagesSearch:
            prevState.page === 1
              ? data.hits
              : [...prevState.imagesSearch, ...data.hits],
          status: 'resolved',
          loading: false,
        }));
      })
      .catch(error => this.setState({ error, status: 'rejected', loading: false }));
  };

  handleLoadMore = () => {
    this.setState(prevState => ({ page: prevState.page + 1 }), () => {
      const { searchName, page } = this.state;
      const KEY = '37071230-d6b04d3068f1a0950a5b376a5';
      this.fetchImages(searchName, page, KEY);
    });
  };

  render() {
    const { imagesSearch, status, loading } = this.state;
    console.log('imagesSearch: ', imagesSearch);

    if (status === 'pending' || loading) {
      return <div>Загрузка</div>;
    }

    if (status === 'rejected') {
      return <div>Все плохо</div>;
    }

    if (status === 'resolved') {
      return (
        <>
          <ul className={css.ImageGallery}>
            {imagesSearch.map(({ id, largeImageURL, tags }) => (
              <ImageGalleryItem key={id} src={largeImageURL} alt={tags} />
            ))}
          </ul>
          {imagesSearch.length > 0 && <Button onClick={this.handleLoadMore} />}
        </>
      );
    }

    return null;
  }
}
