import React, { Component } from 'react';
import css from './ImageGallery.module.css';
import { Button } from 'components/Button/Button';
import { ImageGalleryItem } from 'components/ImageGalleryItem/ImageGalleryItem';
import { Loader } from 'components/Loader/Loader';
import API from '../../servises/api-images.js';

export class ImageGallery extends Component {
  state = {
    searchName: null,
    i: [], // Initialize i as an empty array
    page: 1,
    loading: false,
    error: null,
    status: 'idle',
  };

  componentDidUpdate(prevProps, prevState) {
    const prevName = prevProps.searchName;
    const newName = this.props.searchName;

    const page = this.state.page;
    const name = newName.searchName;

    if (prevName !== newName) {
      // this.setState({ searchName: null, i: [], page: 1 });
      API.fetchImages(name, page)
        .then(data => {
          this.setState({
            i: data.hits,
            status: 'resolved',
            loading: false,
          });
        })
        .catch(error =>
          this.setState({ error, status: 'rejected', loading: false })
        );
    }
  }

  handleLoadMore = () => {
    const name = this.state.searchName;
    const page = this.state.page;

    API.fetchImages(name, page)
      .then(data => {
        this.setState({
          i: [this.state.i, ...data.hits],
          status: 'resolved',
          loading: false,
        });
      })
      .catch(error =>
        this.setState({ error, status: 'rejected', loading: false })
      );

  };

  render() {
    const { i, status, loading } = this.state;

    if (status === 'pending' || loading) {
      return <Loader />;
    }

    if (status === 'rejected') {
      return <div>Все плохо</div>;
    }

    if (status === 'resolved') {
      return (
        <>
          <ul className={css.ImageGallery}>
            {i.map(({ id, largeImageURL, tags }) => (
              <ImageGalleryItem key={id} src={largeImageURL} alt={tags} />
            ))}
          </ul>
          {i.length > 0 && (
            <Button onClick={this.handleLoadMore}>Load more</Button>
          )}
        </>
      );
    }

    return null;
  }
}
