import React, { Component } from 'react';
import css from './ImageGallery.module.css';
import { Button } from 'components/Button/Button';
import { ImageGalleryItem } from 'components/ImageGalleryItem/ImageGalleryItem';
import { Loader } from 'components/Loader/Loader';
import API from '../../servises/api-images.js';

export class ImageGallery extends Component {
  state = {
    searchName: '',
    images: [], // Initialize images as an empty array
    page: 1,
    // loading: false,
    error: null,
    status: 'idle',
    showButton: false,
  };

  componentDidUpdate(prevProps, prevState) {
    const prevName = prevProps.searchName.searchName;
    const newName = this.props.searchName.searchName;

    if (prevName !== newName) {
      this.setState({ images: [], page: 31, loading: true });
      const page = this.state.page;
      console.log('page: ', page);
      setTimeout(() => {
        API.fetchImages(newName, page)
          .then(newImages => {
            console.log(newImages);
            this.setState({
              images: newImages.hits,
              page: page + 1,
              status: 'resolved',
              loading: false,
              showButton: true,
            });
          })
          .catch(error =>
            this.setState({ error, status: 'rejected', loading: false })
          );
      }, 3000);
    }

  }

  handleLoadMore = () => {
    const page = this.state.page;
    console.log('page: ', page);
    const name = this.props.searchName.searchName;
    this.setState({ loading: true });
    API.fetchImages(name, page)
      .then(newImages => {
        console.log(Math.round(newImages.totalHits / 12));
        if (Math.round(newImages.totalHits / 12) === page) {
          return this.setState({
            images: [...this.state.images, ...newImages.hits],
            showButton: false,
          });
        }
        this.setState({
          images: [...this.state.images, ...newImages.hits],
          page: page + 1,
          status: 'resolved',
          loading: false,
        });
      })
      .catch(error =>
        this.setState({ error, status: 'rejected', loading: false })
      );
  };

  render() {
    const { images, status, loading } = this.state;

    if (status === 'pending') {
      return <Loader />;
    }

    if (status === 'rejected') {
      return <div>Все плохо</div>;
    }

    if (status === 'resolved') {
      return (
        <>
          <ul className={css.ImageGallery}>
            {images.map(({ id, largeImageURL, tags }) => (
              <ImageGalleryItem key={id} src={largeImageURL} alt={tags} />
            ))}
          </ul>
          {loading && <Button onClick={this.handleLoadMore}>Load more</Button>}
        </>
      );
    }

    return null;
  }
}
